function getNoiBat(){
  var Books = getDataFromLocal(Books,"sanpham");
  number=1;
while(number<=7){
  var i = Math.floor(Math.random() * Books.length)+1;  
  Books[i].noibat=1;
number++}


  var noibat = Books.filter(book => book.noibat==1);
  setDataToLocal(noibat,"Nổi Bật");
  console.log('set Noi Bat DONE!!!!');
}


